import React, { useEffect, useState } from 'react';
import './App.css';
import Post from './Post';
import { db } from './firebase';
import { makeStyles, Modal } from '@mui/material';
import { ClassNames } from '@emotion/react';

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}`,
    left: `${left}`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyle = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgrooundColor: theme.pallete.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),

  },
}));


function App() {
  const classes = useStyle();
  const [modalStyle] = useState(getModalStyle);
  const [posts, setPosts] = useState([]);
  const [open, setOpen] = useState(false);

  //useEffect -> Runs a piece of code based on condition

  useEffect(() => {
    db.collection('posts').onSnapshot(snapshot => {
      setPosts(snapshot.docs.map(doc => ({
        id: doc.id,
        post: doc.data()
      })));
    })
  }, []);

  return (
    <div className="app">
      <Modal
        open={open}
        onClose={() => setOpen(false)}
      >
        <div style={modalStyle} className={ClassNames.paper}>
          <h2> Im a modal</h2>
        </div>
      </Modal>

      <div className="app__header">
        <img
          className='app__headerImage'
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/2a/Instagram_logo.svg/320px-Instagram_logo.svg.png"
          alt="logo"
        />
      </div>

      <h1>Hello</h1>
      {
        //setting up key allowed me to refresh only uploaded image
        posts.map(({ id, post }) => (
          <Post key={id} username={post.username} caption={post.caption} imageUrl={post.imageUrl} />
        ))
      }

    </div>
  );
}

export default App;
