import React, { useEffect, useState } from 'react';
import './App.css';
import Post from './Post';
import { db } from './firebase';


function App() {
  const [posts, setPosts] = useState([
    // {
    //   username: "edyttabartosiewicz",
    //   caption: "Happy days",
    //   imageUrl: "https://scontent-waw1-1.xx.fbcdn.net/v/t1.6435-9/187384210_3061908687361916_2203947915172169054_n.jpg?_nc_cat=103&ccb=1-7&_nc_sid=09cbfe&_nc_ohc=lq7_y6eSujwAX8K_fqI&_nc_ht=scontent-waw1-1.xx&oh=00_AT9gEg1JB7WbnqXVtnPWxD_1Q3UIcvlo9JfCDoHvFnkN1Q&oe=62D1A368"
    // },
    // {
    //   username: "robertlewandowski",
    //   caption: "Another W",
    //   imageUrl: "https://cdn.vox-cdn.com/thumbor/aLycAglZan3hJv8dpL9sVwEPmh4=/0x0:3086x4629/1200x800/filters:focal(1198x921:1690x1413)/cdn.vox-cdn.com/uploads/chorus_image/image/70982280/1241315585.5.jpg"

    // },
    // {
    //   username: "rock",
    //   caption: "Solid",
    //   imageUrl: "https://i.wpimg.pl/1200x/d.wpimg.pl/1393258079-1484059892/dwayne-the-rock-johnson.jpg"
    // }
  ]);

  //useEffect -> Runs a piece of code based on condition

  useEffect(() => {
    db.collection('posts').onSnapshot(snapshot => {
    setPosts(snapshots.docs.map(doc => doc.data()));
    })
  },[]);

  return (
    <div className="app">

      <div className="app__header">
          <img
          className='app__headerImage'
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/2a/Instagram_logo.svg/320px-Instagram_logo.svg.png"
          alt="logo"
          />
      </div>

      <h1>Hello</h1>
      {
        posts.map(post => (
          <Post username={post.username} caption={post.caption} imageUrl={post.imageUrl}/>
        ))
      }

    </div>
  );
}

export default App;
